<div>
  <h1>Todo List</h1>
  <input [(ngModel)]="newTodoTitle" placeholder="New Todo">
  <button (click)="addTodo()">Add</button>
  <input type="file" (change)="uploadCSV($event)">
  <button (click)="downloadCSV()">Download CSV</button>

  <ul>
    <li> Task Id </li>
    <li *ngFor="let todo of todos | async" style="margin: 2%; padding: 2%;">
      <input [(ngModel)]="todo.taskId" [disabled]="true" style="margin: 2%;">
      <input [(ngModel)]="todo.title" style="margin: 2%;">
      <select [(ngModel)]="todo.status" style="margin: 2%;">
        <option *ngFor="let s of statusList" [value]="s"> {{ s }} </option>
      </select>
      <button (click)="updateTodo(todo)" style="margin: 2%;">Update</button>
      <button (click)="deleteTodo(todo.taskId)" style="margin: 2%;">Delete</button>
    </li>
  </ul>
</div>
