// src/app/todo-list/todo-list.component.ts
import { Component, OnInit } from '@angular/core';
import { TodoService, Todo } from '../_service/todo.service';
// import { saveAs } from 'file-saver';
// import * as Papa from 'papaparse';

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.css']
})
export class TodoListComponent implements OnInit {
  todos: Observable<Todo[]>;
  newTodoTitle = '';

  constructor(private todoService: TodoService) {}

  ngOnInit(): void {
    this.todos = this.todoService.getTodos();
  }

  addTodo() {
    const newTodo: Todo = {
      id: Date.now(),
      title: this.newTodoTitle,
      status: 'pending'
    };
    this.todoService.addTodo(newTodo);
    this.newTodoTitle = '';
  }

  updateTodo(todo: Todo) {
    this.todoService.updateTodo(todo);
  }

  deleteTodo(id: number) {
    this.todoService.deleteTodo(id);
  }

  uploadCSV(event: any) {
    const file = event.target.files[0];
    Papa.parse(file, {
      complete: (result) => {
        const todos: Todo[] = result.data.map((item: any, index: number) => ({
          id: index,
          title: item[0],
          status: item[1]
        }));
        this.todoService.setTodosFromCSV(todos);
      }
    });
  }

  downloadCSV() {
    const todos = this.todoService.getTodosAsCSV();
    const csv = Papa.unparse(todos);
    const blob = new Blob([csv], { type: 'text/csv' });
    saveAs(blob, 'todos.csv');
  }
}
